        } }, !props.holderEdit ?
        React.createElement("div", { onClick: () => props.onHolderEdit(true) }, props.holderName ? props.holderName : 'cardholder name') :
        React.createElement(InputComponent, { scale: props.scale, value: props.holderName, onKeyPress: handleKeyPress, type: "text", fontFamily: props.fontFamily ? props.fontFamily : '', width: `calc(100% - ${110 * (props.scale ? props.scale : 1)}px)`, fontSize: 23 * (props.scale ? props.scale : 1), onBlur: () => props.onHolderEdit(false), onChange: (value) => props.onChange(value, 'holderName') })));
};
export default HolderField;
