import * as React from 'react';
const InputComponent = (props) => {
    const inputRef = React.useRef(null);
    React.useEffect(() => {
        if (inputRef.current)
            inputRef.current.focus();
    }, []);
    const handleChange = (e) => {
        if (props.type === 'number')
            e.target.value ? props.onChange(e.target.value) : props.onChange('0');
        else
            props.onChange(e.target.value);
    };
    return (React.createElement("input", { style: {
            fontSize: props.fontSize,
            fontFamily: props.fontFamily,
            width: props.width ? props.width : '',
            letterSpacing: props.letterSpacing
        }, value: props.value, onKeyPress: e => props.onKeyPress(e), ref: inputRef, type: props.type, onBlur: () => props.onBlur(), onChange: handleChange }));
};
export default InputComponent;
